import React, {userState, useState} from "react";
export default function AddTask({addTask})
{
    const[newTask,setnewTask]=useState({
    title:'',
    description:'',
    is_completed:false
    });
    const handleInput=(event)=>
    {
        const{name,value}=event.target;
        setnewTask((prevTask)=>({
            ...prevTask,
            [name]:value
        }));
    }
    const handleSumbit=async(event)=>
    {
        event.preventDefault();
        try{
            const task=await fetch('http://localhost:8000/apis/todolist/',{
                method:'POST',
                headers:{
                    "Content-Type":"application/json"
                },
                body:JSON.stringify(newTask)
                
            })
            .then(resp=>resp.json)
            .then(data=>{
                return data;
            })
            .catch(err=>console.log('failed to create new task !!!!'));
            addTask(task);
            setnewTask({title:"",description:"",is_completed:false})
        }
        catch(err)
        {
            console.error("Error",err)
        }
    }
        return(
            <div>
                <form className="col-md-6 mx-auto" onSubmit={handleSumbit}>
                    {/*for title*/}
                    <div className="form-group">
                        <label htmlFor="title">Title</label>
                        <input type="text" className="=from-control" id="title"
                        placeholder="Enter task title" name="title" vlaue={newTask.title}
                        required onChange={handleInput}/>
                    </div>
                    {/*for description*/}
                    <div className="form-group">
                        <label htmlFor="title">description</label>
                        <input type="text" className="=from-control" id="description"
                        placeholder="Enter task description" name="description" vlaue={newTask.description}
                        required onChange={handleInput}/>
                    </div>
                    {/*for checkbox*/}
                    <div className="form-check">
                        <input type="checkbox" className="=from-control-input" id="examplecheck1"
                         name="is_completed" vlaue={newTask.is_completed}
                        required onChange={handleInput}/>
                        <label className="form-check-label" htmlFor="examplecheck1">Check me Out</label>
                    </div>
                    <button type="submit" className="btn btn-success">Add Task</button>
                </form>
            </div>
        )
}
